mapper.update:
UPDATE flight


        and flight_number = #{flightNumber}


        and aircraft_model = #{aircraftModel}


        and seat_layout = #{seatLayout}


        and departure_city = #{departureCity}


        and arrival_city = #{arrivalCity}


        and departure_time = #{departureTime}


        and arrival_time = #{arrivalTime}


        and price = #{price},


        total_seats = #{totalSeats}


        and remaining_seats = #{remainingSeats}


        and airline = #{airline}



    id = #{id}

mapper.select:
SELECT * FROM flight


        AND departure_city = #{departureCity}


        AND arrival_city = #{arrivalCity}


        AND departure_time = #{departureTime}


        AND arrival_time = #{arrivalTime}
